descriptor.controllerScript:
self.onInit = function() {
    var attributeService = self.ctx.$scope.$injector.get('attributeService');

    var barData = {
        labels: [],
        datasets: []
    };

    self.ctx.datasources[0].dataKeys.forEach(function(dataKey, index) {
        barData.datasets.push({
            label: dataKey.label,
            data: [],
            backgroundColor: [],
            borderColor: [],
            borderWidth: 1
        });
    });

    self.ctx.datasources.forEach(function(datasource, indexDatasource) {
        barData.labels[indexDatasource] = datasource.name;

        if (self.ctx.settings.entityNameAttribute && datasource.entityId) {
            attributeService.getEntityAttributesValues(datasource.entityType, datasource.entityId, 'SERVER_SCOPE', self.ctx.settings.entityNameAttribute).then(function (attributes) {
                if (attributes && attributes.length > 0) {
                    barData.labels[indexDatasource] = attributes[0].value;
                }
            });
        }

        datasource.dataKeys.forEach(function(dataKey, indexDataKey) {
            barData.datasets[indexDataKey].data[indexDatasource] = 0;
            barData.datasets[indexDataKey].backgroundColor[indexDatasource] = self.ctx.datasources[0].dataKeys[indexDataKey].color;
            barData.datasets[indexDataKey].borderColor[indexDatasource] = self.ctx.datasources[0].dataKeys[indexDataKey].color;
        });
    });

    self.ctx.data.forEach(function(cellData) {
        if (cellData.data.length > 0) {
            var datasetIndex = cellData.datasource.dataKeys.indexOf(cellData.dataKey);
            var dataIndex = self.ctx.datasources.indexOf(cellData.datasource);

            barData.datasets[datasetIndex].data[dataIndex] = parseFloat(cellData.data[cellData.data.length - 1][1]);
        }
    });

    var barChart = $('.barChart', self.ctx.$container);
    self.ctx.chart = new Chart(barChart, {
        type: 'bar',
        data: barData,
        options: {
            responsive: false,
            maintainAspectRatio: false,
            scales: {
                yAxes: [{
                    ticks: {
                        beginAtZero: true
                    }
                }]
            }
        }
    });

    self.onResize();
}

self.onDataUpdated = function() {
    var barData = self.ctx.chart.data;

    self.ctx.data.forEach(function(cellData) {
        if (cellData.data.length > 0) {
            var datasetIndex = cellData.datasource.dataKeys.indexOf(cellData.dataKey);
            var dataIndex = self.ctx.datasources.indexOf(cellData.datasource);

            barData.datasets[datasetIndex].data[dataIndex] = parseFloat(cellData.data[cellData.data.length - 1][1]);
        }
    });

    self.ctx.chart.update();
}

self.typeParameters = function() {
    return {
        maxDatasources: 1
    };
}

self.onResize = function() {
    self.ctx.chart.resize();
}

