descriptor.controllerScript:
self.onInit = function() {
    console.log('onInit');
    console.log(self.ctx);
    let attributeService = self.ctx.$scope.$injector.get('attributeService');
    let columns = [];
    let dataSet = [];

    columns.push({ title: self.ctx.settings.entityColumnTitle||'Entities' });
    self.ctx.datasources[0].dataKeys.forEach(function(dataKey) {
        columns.push({ 
            title: dataKey.label,
            searchable: false,
            render: function (data, type, row, meta) {
                if (type === 'display' && typeof data === 'number') {
                    let decimals = self.ctx.decimals||(data.toString().split('.')[1]||'').length;
                    let units = self.ctx.units;
                    if (dataKey.decimals || dataKey.decimals === 0) {
                        decimals = dataKey.decimals;
                    }
                    if (dataKey.units) {
                        units = dataKey.units;
                    }
                    return self.ctx.utils.formatValue(data, decimals, units, true);
                } else {
                    return data;
                }
            }
        });
    });

    self.ctx.datasources.forEach(function(datasource, indexDatasource) {
        dataSet[indexDatasource] = [];
        dataSet[indexDatasource][0] = datasource.name;

        self.ctx.datasources[indexDatasource].dataKeys.forEach(function(dataKey, indexDataKey) {
            dataSet[indexDatasource][indexDataKey + 1] = null;
        });

        if (self.ctx.settings.entityNameAttribute && datasource.entityId) {
            attributeService.getEntityAttributesValues(datasource.entityType, datasource.entityId, 'SERVER_SCOPE', self.ctx.settings.entityNameAttribute).then(function (attributes) {
                if (attributes && attributes.length > 0) {
                    dataSet[indexDatasource][0] = attributes[0].value;

                    //if(indexDatasource === self.ctx.datasources.length - 1) {
                    //    self.ctx.datatable.rows().invalidate().draw(false);
                    //}
                }
            });
        }
    });

    self.ctx.columns = columns;
    self.ctx.dataSet = dataSet;
    self.ctx.datatable = $('table', self.ctx.$container).DataTable({
        data: dataSet,
        columns: columns,
        searching: self.ctx.settings.enableSearch,
        paging: self.ctx.settings.displayPagination,
        lengthChange: false,
        pageLength: self.ctx.settings.defaultPageSize,
        scrollX: true,
        scrollY: "50px",
        scrollCollapse: true
    });

    self.onDataUpdated();
    self.onResizeScrollY();
}

self.onDataUpdated = function() {
    console.log('onDataUpdated');
    console.log(self.ctx);
    self.ctx.data.forEach(function(cellData) {
        if (cellData.data.length > 0) {
            let rowIndex =  self.ctx.datasources.indexOf(cellData.datasource);
            let colIndex = cellData.datasource.dataKeys.indexOf(cellData.dataKey) + 1;
            let cellValue = null;

            switch(self.ctx.defaultSubscription.subscriptionTimewindow.aggregation.type) {
              case 'MIN':
                cellValue = cellData.data
                    .map(function(data) {
                        return data[1];
                    })
                    .reduce(function(lastData, currentData) {
                        return (lastData<currentData)?lastData:currentData;
                    });
                break;
              case 'MAX':
                cellValue = cellData.data
                    .map(function(data) {
                        return data[1];
                    })
                    .reduce(function(lastData, currentData) {
                        return (lastData>currentData)?lastData:currentData;
                    });
                break;
              case 'AVG':
                cellValue = cellData.data
                    .map(function(data) {
                        return data[1];
                    })
                    .reduce(function (lastData, currentData) {
                        lastData += currentData;
                        return lastData;
                    })/cellData.data.length;
                break;
              case 'SUM':
                cellValue = cellData.data
                    .map(function(data) {
                        return data[1];
                    })
                    .reduce(function (lastData, currentData) {
                        lastData += currentData;
                        return lastData;
                    });
                break;
              case 'COUNT':
                cellValue = cellData.data
                    .map(function(data) {
                        return data[1];
                    })
                    .reduce(function (lastData, currentData) {
                        lastData += currentData;
                        return lastData;
                    });
                break;
              default:
                cellValue = cellData.data[cellData.data.length - 1][1];
            }

            self.ctx.dataSet[rowIndex][colIndex] = cellValue;
        }
    });

    self.ctx.datatable.rows().invalidate().draw(false);
}

self.onResize = function() {
    console.log('onResize');
    self.onResizeScrollY();

    self.ctx.datatable.draw('page');
}

self.onResizeScrollY = function() {
    let scrollY = self.ctx.$container.height() - ($('div.dataTables_wrapper', self.ctx.$container).height() - $('div.dataTables_scrollBody', self.ctx.$container).outerHeight()) - 10;
    scrollY = (scrollY>50)?scrollY:50;
    $('div.dataTables_scrollBody', self.ctx.$container).css('maxHeight', scrollY + 'px');
}

self.typeParameters = function() {
    return {
        maxDatasources: 1
    };
}

self.onDestroy = function() {
    console.log('onDestroy');
    self.ctx.datatable.destroy();
}

